package com.zjf.graphx.learning

import org.apache.spark.graphx.GraphLoader
import org.apache.spark.{HashPartitioner, SparkConf, SparkContext}

object PageRank {
  def main(args: Array[String]): Unit = {
    val conf = new SparkConf().setAppName("PageRank").setMaster("local[*]")
    val sc = new SparkContext(conf)

    // Load the edges as a graph
    val graph = GraphLoader.edgeListFile(sc, "/Users/zhangjianfeng/workspaces/workspace_github_bg/my-spark-graphx/data/followers.txt")
    // Run PageRank
    //0.0001为前后两次收敛的误差阈值，小于这个阈值时则结束计算
    //    val ranks = graph.pageRank(0.0001).vertices
    val pageRank = graph.pageRank(0.0001)
    val gcc = graph.connectedComponents()
    val ranks = pageRank.vertices
    val trip = pageRank.triplets
    gcc.vertices.foreach(x =>{
      println(x._1,x._2)
    })
    println("------------------------")

    trip.foreach(trip =>{
      println(trip.srcId,trip.dstId,trip. srcAttr,trip.dstAttr,trip.attr)
    })

    println("------------rank------------")

    ranks.foreach(println(_))
    println("-----------------------------")

    // Join the ranks with the usernames
    val users = sc.textFile("/Users/zhangjianfeng/workspaces/workspace_github_bg/my-spark-graphx/data/users.txt").map { line =>
      val fields = line.split(",")
      (fields(0).toLong, fields(1))
    }
    val ranksByUsername = users.join(ranks).map {
      case (id, (username, rank)) => (username, rank)
    }
    println(ranksByUsername.sortBy(_._2, false).collect().mkString("\n"))

